  Row,
  Col,
  Image,
  ListGroup,
  Card,
  Button,
  Form,
} from 'react-bootstrap';

function ProductScreen({ match }, props) {
  console.log(props);

  const product = products.find((p) => p._id == match.params.id);
  return <div>product {product.name} </div>;
}

export default ProductScreen;
